import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WordRevision.css';

function WordRevision() {
  const [words, setWords] = useState([]);
  const [currentWord, setCurrentWord] = useState(null);
  const [score, setScore] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [feedback, setFeedback] = useState('');
  const [category, setCategory] = useState('verbs'); // Default category

  useEffect(() => {
    axios.get(`http://localhost:5000/api/japanese-words/${category}`)
      .then(response => {
        setWords(response.data);
        setCurrentWord(getRandomWord(response.data));
      })
      .catch(error => console.error('Error fetching words:', error));
  }, [category]);

  const getRandomWord = (wordsArray) => {
    const randomIndex = Math.floor(Math.random() * wordsArray.length);
    return wordsArray[randomIndex];
  };

  const checkAnswer = () => {
    if (currentWord && userInput.toLowerCase() === currentWord.romaji.toLowerCase()) {
      setFeedback('Correct!');
      setScore(score + 1);
    } else {
      setFeedback(`Incorrect. The correct answer is ${currentWord.romaji}.`);
    }
  };

  const nextWord = () => {
    setCurrentWord(getRandomWord(words));
    setUserInput('');
    setFeedback('');
  };

  return (
    <div className="word-revision-container">
      <h2>Word Revision</h2>

      {/* Category Selection */}
      <div>
        <label htmlFor="category">Choose a category:</label>
        <select
          id="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="verb">Verbs</option>
          <option value="animals">Animals</option>
          <option value="adjectives">Adjectives</option>
          <option value="numbers">Numbers</option>
        </select>
      </div>

      {currentWord ? (
        <div className="word-revision">
          <div className="word">
            <h3>Kanji: {currentWord.kanji}</h3>
            <h4>Hiragana: {currentWord.hiragana}</h4>
            <p>Meaning: {currentWord.meaning}</p>
            <input
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Type the romaji"
            />
            <button onClick={checkAnswer}>Check Answer</button>
            <button onClick={nextWord}>Next Word</button>
            <p>{feedback}</p>
          </div>
          <div className="score">
            <h4>Score: {score}</h4>
          </div>
        </div>
      ) : (
        <p>Loading words...</p>
      )}
    </div>
  );
}

export default WordRevision;
