import React, { useState, useEffect } from 'react';
import axios from 'axios';


function Vocabulary() {
  const [level, setLevel] = useState('');
  const [vocabList, setVocabList] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchVocab = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`http://localhost:5000/vocab?level=${level}`);
      setVocabList(response.data.filtered_vocab || []);
    } catch (err) {
      setError('Failed to fetch vocabulary. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleLevelChange = (e) => {
    setLevel(e.target.value);
  };

  const handleSearch = () => {
    if (level) {
      fetchVocab();
    } else {
      setError('Please select a level');
    }
  };

  return (
    <div className="vocabulary-container">
      <h1>Vocabulary by Level</h1>
      <div className="input-section">
        <label htmlFor="level">Select Level: </label>
        <input
          type="number"
          id="level"
          value={level}
          onChange={handleLevelChange}
          placeholder="Enter a level (e.g., 5)"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      <div className="vocabulary-list">
        {vocabList.map((vocab, index) => (
          <div key={index} className="vocab-item">
            <h3>{vocab.data.characters}</h3>
            <p>Meaning: {vocab.data.meanings.map((m) => m.meaning).join(', ')}</p>
            <p>Reading: {vocab.data.readings.map((r) => r.reading).join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Vocabulary;
