import React, { useState } from 'react';
import axios from 'axios';

function Vocabulary() {
  const [level, setLevel] = useState('');
  const [vocabList, setVocabList] = useState([]);
  const [error, setError] = useState(null);

  const fetchVocab = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/vocab?level=${level}`);
      console.log(response)
      setVocabList(response.data);  // Set the vocabulary list
    } catch (err) {
      setError('Failed to fetch vocabulary.');
    }
  };

  const handleLevelChange = (e) => {
    setLevel(e.target.value);
  };

  const handleSearch = () => {
    if (level) {
      fetchVocab();
    } else {
      setError('Please select a level');
    }
  };

  return (
    <div>
      <input
        type="number"
        value={level}
        onChange={handleLevelChange}
        placeholder="Enter level (e.g., 1)"
      />
      <button onClick={handleSearch}>Fetch Vocabulary</button>

      {error && <p>{error}</p>}

      <div>
        {vocabList.map((vocab) => (
          <div key={vocab.id}>
            <h3>{vocab.data.characters}</h3>
            <p>{vocab.data.meanings.map((meaning) => meaning.meaning).join(', ')}</p>
            <p>Reading: {vocab.data.readings.map((reading) => reading.reading).join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Vocabulary;
