import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WordRevision.css';

function WordRevision() {
  const [words, setWords] = useState([]);
  const [category, setCategory] = useState('verbs'); // Default category
  const [error, setError] = useState(null);

  const fetchWords = async () => {
    if (!category) {
      setError('Please select a category');
    }

    try {
      const response = await axios.get(`http://localhost:5000/api/japanese-words?category=${category}`);
      setWords(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch words');
    }
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleSearch = () => {
    fetchWords();
  };

  return (
    <div className="word-revision-container">
      <h2>Word Revision</h2>

      {/* Category Selection */}
      <div className="category-selection">
        <label htmlFor="category">Choose a category:</label>
        <select
          id="category"
          value={category}
          onChange={handleCategoryChange}
        >
          <option value="verbs">Verbs</option>
          <option value="animals">Animals</option>
          <option value="adjectives">Adjectives</option>
          <option value="numbers">Numbers</option>
        </select>
      </div>

      {/* Search Button */}
      <div className="search-button">
        <button onClick={handleSearch}>Load Words</button>
      </div>

      {/* Error Message */}
      {error && <p className="error">{error}</p>}

      {/* Words List */}
      {words.length > 0 ? (
        <div className="word-revision">
          {words.map((word, index) => (
            <div key={index} className="word-card">
              <h3>Kanji: {word.kanji}</h3>
              <h4>Hiragana: {word.hiragana}</h4>
              <p>Meaning: {word.meaning}</p>
              <p>Romaji: {word.romaji}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="loading">Loading words...</p>
      )}
    </div>
  );
}

export default WordRevision;
