import React, { useState } from 'react';
import './Flashcards.css'; // We'll create the CSS for the flip effect

function Flashcards({ characters }) {
  const [flipped, setFlipped] = useState(false);

  const handleClick = () => {
    setFlipped(!flipped);
  };

  if (!Array.isArray(characters)) {
    return <div>Loading...</div>; // Show loading state if data is not available
  }

  return (
    <div className="flashcard-container">
      {characters.map((card, index) => (
        <div className="flashcard" key={index} onClick={handleClick}>
          <div className={`flashcard-inner ${flipped ? 'flipped' : ''}`}>
            <div className="flashcard-front">
              <h2>{card.character}</h2> {/* Japanese character (Hiragana/Katakana) */}
            </div>
            <div className="flashcard-back">
              <h3>{card.romaji}</h3> {/* English sound (romaji) */}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Flashcards;
