import React, { useEffect, useState } from 'react';
import './RandomWord.css'; // Optional for styling

function RandomWord() {
    const [wordData, setWordData] = useState(null)
    const [error, setError] = useState(null)
    const [loading, setLoading] = useState(true)

    const fetchRandomWord = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await fetch('http://localhost:5000/api/japanese/random_word');
            if (!response.ok) {
              throw new Error(`Error: ${response.statusText}`);
            }
            const data = await response.json();
            setWordData(data);
          } catch (err) {
            setError(err.message);
          } finally {
            setLoading(false);
          }
    }

    const speakWord = (word) => {
      const utterance  = new SpeechSynthesisUtterance(word)
      const voices = speechSynthesis.getVoices()
      utterance.lang = "ja-JP";
      utterance.rate = 0.8; 
      utterance.pitch = 0.5;
      utterance.voice = voices[6]
      speechSynthesis.speak(utterance)
      
    }

    useEffect(() => {
        fetchRandomWord();
    }, []); 

    return (
        <div className="random-word-container">
          {loading && <p>Loading...</p>}
          {error && <p>Error: {error}</p>}
          {wordData && (
            <div className="random-word">
              <h1>{wordData.word || 'No Word Available'}</h1>
              <p>Reading: {wordData.reading}</p>
              <p>Definition: {wordData.definition}</p>
              <p>Romaji: {wordData.romaji}</p>
              <button onClick={fetchRandomWord}>Get Another Word</button>
              <button onClick={() => speakWord(wordData.romaji)}>How to pronounce</button>
            </div>
          )}
        </div>
      );
}

export default RandomWord;
