import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Flashcards.css'; // Make sure you import the CSS for styling

function Hiragana() {
  const [hiragana, setHiragana] = useState([]);
  const [flipped, setFlipped] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/japanese/characters')
      .then(response => {
        setHiragana(response.data.hiragana); // Assuming 'hiragana' is the array key
      })
      .catch(error => console.error(error));
  }, []);

  // Function to handle individual card flip
  const handleClick = (index) => {
    setFlipped((prevState) => ({
      ...prevState,
      [index]: !prevState[index], // Toggle flip state on click
    }));
  };

  // Function to reset flip state of all cards
  const resetFlipState = () => {
    setFlipped({});
  };

  return (
    <div>
      <h1>Hiragana</h1>
      <button onClick={resetFlipState} className="reset-button">
        Show Japanese Characters
      </button>
      <div className="character-list">
        {hiragana.map((card, index) => (
          <div key={index} className="character" onClick={() => handleClick(index)}>
            <div className={`character-inner ${flipped[index] ? 'flipped' : ''}`}>
              <div className="character-front">
                <h2>{card.character}</h2>
              </div>
              <div className="character-back">
                <h3>{card.romaji}</h3>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Hiragana;
