import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Quiz.css'

function HiraganaQuiz() {
    const [hiragana, setHiragana] = useState([]);
    const [currentFlashcard, setCurrentFlashcard] = useState(null);
    const [userInput, setUserInput] = useState('');
    const [feedback, setFeedback] = useState('');
    const [score, setScore] = useState(0);
    const [questionCount, setQuestionCount] = useState(0);


  useEffect(() => {
    axios.get('http://localhost:5000/api/japanese/characters')
      .then(response => {
        setHiragana(response.data.hiragana); 
        setCurrentFlashcard(getRandomFlashcard(response.data.hiragana));
      })
      .catch(error => console.error(error));
  }, []);

  const getRandomFlashcard = (hiraganaArray) => {
    const randomIndex = Math.floor(Math.random() * hiraganaArray.length);
    return hiraganaArray[randomIndex];
};


const checkAnswer = () => {
    if (currentFlashcard && userInput.toLowerCase() === currentFlashcard.romaji) {
        setFeedback('Correct!');
    } else {
        setFeedback(`Incorrect. The correct answer is ${currentFlashcard?.romaji}.`);
    }
};

const nextFlashcard = () => {
    setCurrentFlashcard(getRandomFlashcard(hiragana));
    setUserInput(''); 
    setFeedback(''); 
};

return (
    <div className="flashcard-quiz-container">
      {currentFlashcard ? (
        <div className="flashcard-quiz">
          <div className="flashcard">
            <h1 className="flashcard-character">{currentFlashcard.character}</h1>
            <input
              className="user-input"
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Type the romaji"
            />
            <div className="feedback-container">
              <button className="check-button" onClick={checkAnswer}>Check Answer</button>
              <button className="next-button" onClick={nextFlashcard}>Next</button>
              <p className="feedback">{feedback}</p>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading flashcards...</p>
      )}
    </div>
  );
}

export default HiraganaQuiz
