import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Nav.css'
function Navigation() {
  const location = useLocation();
  const navigate = useNavigate();
  const username = localStorage.getItem('user')?.replace(/['"]+/g, '');

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login'); // Redirect to login page after logout
  };


  return (
    <nav>
      {location.pathname !== "/" && <Link to="/">Home</Link>}
      {location.pathname !== "/hiragana" && <Link to="/hiragana">Hiragana</Link>}
      {location.pathname !== "/katakana" && <Link to="/katakana">Katakana</Link>}
      {location.pathname !== "/login" && <Link to="/login">Login</Link>}
      {location.pathname !== "/signup" && <Link to="/signup">Signup</Link>}
      {username && (
        <>
          <p className="username-display">Welcome, {username}!</p>
          <button onClick={handleLogout}>Logout</button>
        </>
      )}
    </nav>
  );
}

export default Navigation;
