import React, { useState } from 'react';
import './Flashcards.css'; // We'll create the CSS for the flip effect

function Flashcards({ hiragana, katakana }) {
  const [flippedHiragana, setFlippedHiragana] = useState({});
  const [flippedKatakana, setFlippedKatakana] = useState({});

  const handleClick = (index, group) => {
    if (group === 'hiragana') {
      setFlippedHiragana((prevState) => ({
        ...prevState,
        [index]: !prevState[index], // Toggle the flip state for the clicked card in Hiragana
      }));
    } else if (group === 'katakana') {
      setFlippedKatakana((prevState) => ({
        ...prevState,
        [index]: !prevState[index], // Toggle the flip state for the clicked card in Katakana
      }));
    }
  };

  // Render a loading state if no characters are passed
  if (!Array.isArray(hiragana) || !Array.isArray(katakana)) {
    return <div>Loading...</div>;
  }

  return (
    <div className="flashcards-wrapper">
      {/* Hiragana Flashcards */}
      <div className="flashcard-group">
        <h2>Hiragana</h2>
        <div className="flashcard-container">
          {hiragana.map((card, index) => (
            <div
              className="flashcard"
              key={index}
              onClick={() => handleClick(index, 'hiragana')} // Flip the Hiragana card when clicked
            >
              <div
                className={`flashcard-inner ${flippedHiragana[index] ? 'flipped' : ''}`}
              >
                <div className="flashcard-front">
                  <h2>{card.character}</h2> {/* Japanese character (Hiragana) */}
                </div>
                <div className="flashcard-back">
                  <h3>{card.romaji}</h3> {/* English sound (romaji) */}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Katakana Flashcards */}
      <div className="flashcard-group">
        <h2>Katakana</h2>
        <div className="flashcard-container">
          {katakana.map((card, index) => (
            <div
              className="flashcard"
              key={index}
              onClick={() => handleClick(index, 'katakana')} // Flip the Katakana card when clicked
            >
              <div
                className={`flashcard-inner ${flippedKatakana[index] ? 'flipped' : ''}`}
              >
                <div className="flashcard-front">
                  <h2>{card.character}</h2> {/* Japanese character (Katakana) */}
                </div>
                <div className="flashcard-back">
                  <h3>{card.romaji}</h3> {/* English sound (romaji) */}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Flashcards;
