import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WordRevision.css';

function WordRevision() {
  const [words, setWords] = useState([]);
  const [category, setCategory] = useState('verbs'); // Default category
  const [error, setError] = useState(null);
    const fetchWords = async () => {
        if (!category) {
            setError('Please select a category')
        }

        try {
            const response = await axios.get(`http://localhost:5000/api/japanese-words/${category}`);
            setWords(response.data)
            setError(null)
        } catch (err) {
            setError('Failed to fetch words')
        }
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value)
    }

    const handleSearch = () => {
        fetchWords();
    }

    return (
        <div className="word-revision-container">
          <h2>Word Revision</h2>
    
          {/* Category Selection */}
          <div>
            <label htmlFor="category">Choose a category:</label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            >
              <option value="verbs">Verbs</option>
              <option value="animals">Animals</option>
              <option value="adjectives">Adjectives</option>
              <option value="numbers">Numbers</option>
            </select>
          </div>
    
          {words.length > 0 ? (
            <div className="word-revision">
              <div className="word">
                <h3>Kanji: {words[currentWordIndex].kanji}</h3>
                <h4>Hiragana: {words[currentWordIndex].hiragana}</h4>
                <p>Meaning: {words[currentWordIndex].meaning}</p>
                <input
                  type="text"
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  placeholder="Type the romaji"
                />
                <button onClick={checkAnswer}>Check Answer</button>
                <button onClick={nextWord}>Next Word</button>
                <p>{feedback}</p>
              </div>
              <div className="score">
                <h4>Score: {score}</h4>
              </div>
            </div>
          ) : (
            <p>Loading words...</p>
          )}
        </div>
      );

}

export default WordRevision;
